import mongoose, {mongo} from 'mongoose';
import { initializeData } from '../scripts/dbinit';
require('dotenv').config();  

mongoose.connect('mongodb://localhost:27017/Fashion_Fusion');

const db = mongoose.connection;

db.on('error', (error) => {
    console.error(error);
});

db.once('open', () => {
    console.log('Database connection established successfully!');
});

// Call the new function to insert data
initializeData().then(data => {
    // Further actions with the data, like saving to the database
  }).catch(err => {
    console.error("Error initializing data:", err);
  });

export default db;

