export const users = [
    { username: 'Joe', email: 'joe@example.com' },
    { username: 'Charlie', email: 'charlie@example.com' },
    { username: 'Simon', email: 'simon@example.com' },
    { username: 'Teresa', email: 'teresa@example.com'},
    { username: 'Sara', email: 'sara@example.com' },
    { username: 'Dalton', email: 'dalton@example.com' },
    { username: 'Sandy', email: 'sandy@example.com' },
    { username: 'Ambreen', email: 'ambreen@example.com' }
];

export const posts = [
    {
      user: 'Joe', // This should match one of the user names in the users list
      content: 'This is a post about my trip to Vancouver!',
      imageUrl: 'https://example.com/images/vancouver.jpg',
      likes: 5,
      comments: [
        { user: 'Charlie', content: 'Looks awesome!' },
        { user: 'Sara', content: 'Great place to visit!' }
      ]
    },
    {
      user: 'Sara',
      content: 'Exploring the fashion scene in New York!',
      imageUrl: 'https://example.com/images/nyc.jpg',
      likes: 3,
      comments: [
        { user: 'Dalton', content: 'Thats inspiring!' },
        { user: 'Simon', content: 'I want to visit soon!' }
      ]
    },
    {
      user: 'Simon',
      content: 'Had an amazing day in Boston.',
      imageUrl: 'https://example.com/images/boston.jpg',
      likes: 2,
      comments: [
        { user: 'Joe', content: 'Boston is so beautiful!' },
        { user: 'Teresa', content: 'Wish I could be there!' }
      ]
    }
];

// commentData.js
export const comments = [
    { user: "Joe", content: "This is amazing!" },
    { user: "Charlie", content: "So cool!" },
    { user: "Sara", content: "I want to try this!" }
];

