sourcesContent:
const express = require('express');
const bcrypt = require('bcryptjs');
const jwt = require('jsonwebtoken');
const User = require('../model/User');
const router = express.Router();

// POST /register - Register a new user
router.post('/users/register', async (req, res) => {
  try {
    const { username, email, password, profile } = req.body;

    // Check if the user already exists
    const existingUser = await User.findOne({ email });
    if (existingUser) {
      return res.status(400).json({ message: 'User with this email already exists.' });
    }

    // Hash the password before saving it
    const hashedPassword = await bcrypt.hash(password, 10);

    // Create a new user document
    const user = new User({
      username,
      email,
      password: hashedPassword,
      profile
    });

    // Save the user to the database
    await user.save();

    // Send success response
    res.status(201).json({ message: 'User registered successfully!' });
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// POST /login - Authenticate user and return a token
router.post('/users/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    // Find user by email
    const user = await User.findOne({ email });
    if (!user) {
      return res.status(401).json({ message: 'Invalid email or password' });
    }

    // Compare the given password with the hashed password
    const isMatch = await bcrypt.compare(password, user.password);
    if (!isMatch) {
      return res.status(401).json({ message: 'Invalid email or password' });
    }

    // Generate a JWT token
    const token = jwt.sign({ userId: user._id }, process.env.JWT_SECRET, { expiresIn: '1h' });

    // Send the token to the client
    res.json({ token });
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// GET /user/:userId - Fetch user profile details
router.get('/users/:userId', async (req, res) => {
  try {
    const user = await User.findById(req.params.userId).select('-password'); // Don't send the password field
    if (!user) {
      return res.status(404).json({ message: 'User not found' });
    }

    res.json(user); // Send user details except password
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

module.exports = router;

