    type: String
  },
  likes: {
    type: Number,
    "default": 0
  },
  comments: [{
    user: String,
    content: String
  }]
}, {
  timestamps: true
});
var PostModel = mongoose.model('Post', postSchema);
module.exports = {
  PostModel: PostModel
};
