sourcesContent:
import mongoose from 'mongoose';
import { CommentModel } from "../communityEngagement/model/Comment.js";
import { UserModel } from "../auth/model/User.js";
import { PostModel } from "../communityEngagement/model/Post.js";
import { users, posts, comments } from './data';

// Database seeding function
export const populateDatabase = async () => {
    const db = mongoose.connection;

    db.on('error', (error) => {
        console.error(error);
    });

    db.once('open', async () => {
        console.log('Database connection open');
        
        try {
            await UserModel.insertMany(users);
            console.log('Users inserted successfully');
        } catch (error) {
            console.error('Error inserting users:', error);
        }

        try {
            await PostModel.insertMany(posts);
            console.log('Posts inserted successfully');
        } catch (error) {
            console.error('Error inserting posts:', error);
        }

        try {
            await CommentModel.insertMany(comments);
            console.log('Comments inserted successfully');
        } catch (error) {
            console.error('Error inserting comments:', error);
        }
    });
};

