sourcesContent:
import mongoose from 'mongoose';
import { CommentModel } from "../communityEngagement/model/Comment.js";
import { UserModel } from "../auth/model/User.js";
import { PostModel } from "../communityEngagement/model/Post.js";
import { users, posts, comments } from './data';

// Database seeding function
export const populateDatabase = async () => {
    const db = mongoose.connection;

    db.on('error', (error) => {
        console.error(error);
    });

    db.once('open', async () => {
        console.log('Database connection open');
        
        try {
            // Insert Users
            await UserModel.insertMany(users);
            console.log('Users inserted successfully');
        } catch (error) {
            console.error('Error inserting users:', error);
        }

        try {
            // Insert Posts and retrieve their generated IDs
            const savedPosts = await PostModel.insertMany(posts);
            console.log('Posts inserted successfully');

            // Map post content to their generated IDs for easy lookup
            const postIdMap = {};
            savedPosts.forEach(post => {
                postIdMap[post.content] = post._id;  // Assuming 'content' is unique in posts data
            });

            // Update comments array with correct postId by matching post content
            const updatedComments = comments.map(comment => {
                const postId = postIdMap[comment.postContent];  // Assuming each comment has 'postContent'
                return { ...comment, postId };
            });

            // Insert Comments with correct postId references
            await CommentModel.insertMany(updatedComments);
            console.log('Comments inserted successfully');
        } catch (error) {
            console.error('Error inserting posts or comments:', error);
        }
    });
};

