var mongoose = require('mongoose');
var commentSchema = new mongoose.Schema({
  postId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Post',
    required: true
  },
  user: {
    type: String,
    required: true
  },
  content: {
    type: String,
    required: true
  }
}, {
  timestamps: true
});
var CommentModel = mongoose.model('Comment', commentSchema);
module.exports = {
  CommentModel: CommentModel
};
