// Post.js
var mongoose = require('mongoose');
var postSchema = new mongoose.Schema({
  user: {
    type: String,
    required: true
  },
  content: {
    type: String,
    required: true
  },
  imageUrl: {
    type: String
  },
  likes: {
    type: Number,
    "default": 0
  },
  comments: [{
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Comment'
  }] // Add this field
}, {
  timestamps: true
});
var PostModel = mongoose.model('Post', postSchema);
module.exports = {
  PostModel: PostModel
};
