const express = require('express');
const router = express.Router();

// GET route to fetch comments for a specific post
router.get('/posts/:id/comments', async (req, res) => {
    const { id } = req.params;
  
    try {
      const post = await Post.findById(id); // Find the post by its ID
      if (!post) {
        return res.status(404).json({ error: 'Post not found' });
      }
      res.status(200).json(post.comments); // Send comments of the post
    } catch (error) {
      res.status(500).json({ error: 'Server error while fetching comments' });
    }
  });
  
  // POST route to add a new comment to a specific post
  router.post('/posts/:id/comments', async (req, res) => {
    const { id } = req.params;
    const { text } = req.body;
  
    try {
      const post = await Post.findById(id); // Find the post by its ID
      if (!post) {
        return res.status(404).json({ error: 'Post not found' });
      }
  
      // Create a new comment and push it to the post's comments array
      const newComment = { text };
      post.comments.push(newComment);
  
      await post.save(); // Save the post with the new comment
      res.status(201).json(newComment); // Send back the newly added comment
    } catch (error) {
      res.status(500).json({ error: 'Server error while adding comment' });
    }
  });
  
  // DELETE route to remove a comment from a post
  router.delete('/posts/:postId/comments/:commentId', async (req, res) => {
    const { postId, commentId } = req.params;
  
    try {
      const post = await Post.findById(postId);
      if (!post) {
        return res.status(404).json({ error: 'Post not found' });
      }
  
      // Remove the comment by its ID from the comments array
      const commentIndex = post.comments.findIndex((comment) => comment._id.toString() === commentId);
      if (commentIndex === -1) {
        return res.status(404).json({ error: 'Comment not found' });
      }
  
      post.comments.splice(commentIndex, 1); // Remove the comment from the array
      await post.save(); // Save the updated post
      res.status(200).json({ message: 'Comment deleted successfully' });
    } catch (error) {
      res.status(500).json({ error: 'Server error while deleting comment' });
    }
  });
  
  module.exports = router;

